// User registration form javascript validation


// Function to update the input field value
function updateInputFieldValue() {
var markedCheckbox = document.getElementsByName('goto');
var resultField = document.getElementById('result');
resultField.value = '0'; // Default value when no checkbox is checked

for (var checkbox of markedCheckbox) {
    if (checkbox.checked) {
    resultField.value = '1';
    break; // Exit the loop as soon as a checked checkbox is found
    }
}
}

// Attach the update function to the change event of checkboxes
var checkboxes = document.getElementsByName('goto');
for (var checkbox of checkboxes) {
checkbox.addEventListener('change', updateInputFieldValue);
}
   




