//  for contact number validation only numbers allow

document.getElementById('val-currency').addEventListener('input', function (e) {
    var inputValue = e.target.value;
    
    // Remove non-digit characters
    var sanitizedInput = inputValue.replace(/\D/g, '');
    
    // Allow only 10 digits
    var maxLength = 10;
    var truncatedInput = sanitizedInput.slice(0, maxLength);
    
    // Update the input value
    e.target.value = truncatedInput;
  });




// for full name only allow charectors
  document.getElementById('val-fullname').addEventListener('input', function (e) {
    var inputValue = e.target.value;
    
    // Remove non-alphabetic characters and spaces
    var sanitizedInput = inputValue.replace(/[^a-zA-Z\s]/g, '');
  
    // Limit to a minimum of 2 characters
    var minLength = 2;
    if (sanitizedInput.length < minLength) {
      sanitizedInput = sanitizedInput.slice(0, minLength);
    }
  
    // Update the input value
    e.target.value = sanitizedInput;
  });